import { LoadingButton } from "@mui/lab";
import { Box, Button, Card, FormHelperText } from "@mui/material";
import FlexRowAlign from "components/flexbox/FlexRowAlign";
import AppTextField from "components/input-fields/AppTextField";
import { H1, Small } from "components/Typography";
import { useFormik } from "formik";
import { useState } from "react";
import toast from "react-hot-toast";
import { Link } from "react-router-dom";
import * as Yup from "yup";

const ForgetPassword = () => {
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const history = useHistory();

  const initialValues = {
    email: "demo@example.com",
    submit: null
  };

  const validationSchema = Yup.object().shape({
    email: Yup.string().email("Must be a valid email").max(255).required("Email is required")
  });

  const handleSubmit = async (values) => {
    setLoading(true);
    try {
      // Simulating asynchronous operation
      await new Promise(resolve => setTimeout(resolve, 1000));
      setLoading(false);
      toast.success("Reset link has been sent!");
      // Redirect to login page
      history.push("/login");
    } catch (error) {
      setError("Error!");
      setLoading(false);
    }
  };

  const formik = useFormik({
    initialValues,
    validationSchema,
    onSubmit: handleSubmit
  });

  return (
    <FlexRowAlign height="100vh" flexDirection="column">
      <Card sx={{
        padding: 4,
        maxWidth: 600,
        marginTop: 4,
        boxShadow: 1
      }}>
        <H1 fontSize={18} fontWeight={500}>
          Reset your password
        </H1>

        <form noValidate onSubmit={formik.handleSubmit} style={{ width: "100%" }}>
          <AppTextField
            fullWidth
            name="email"
            type="email"
            label="Email"
            onBlur={formik.handleBlur}
            onChange={formik.handleChange}
            value={formik.values.email || ""}
            error={Boolean(formik.touched.email && formik.errors.email)}
            helperText={formik.touched.email && formik.errors.email}
          />

          {error && (
            <FormHelperText error sx={{
              mt: 2,
              fontSize: 13,
              fontWeight: 500,
              textAlign: "center"
            }}>
              {error}
            </FormHelperText>
          )}

          <Box sx={{ mt: 4 }}>
            {loading ? (
              <LoadingButton loading fullWidth variant="contained">
                Reset
              </LoadingButton>
            ) : (
              <Button fullWidth type="submit" variant="contained">
                Reset
              </Button>
            )}
          </Box>
        </form>

        <Small margin="auto" mt={3} color="text.disabled">
          Don't have an account?{" "}
          <Link to="/register" style={{ display: "inline-block" }}>
            <Small color="primary.main">Create an account</Small>
          </Link>
        </Small>
      </Card>
    </FlexRowAlign>
  );
};

export default ForgetPassword;
