import { LoadingButton } from "@mui/lab";
import { Box, Button, Card, Checkbox, Divider, FormControlLabel, FormHelperText, Radio, RadioGroup, Typography } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";
import { useFormik } from "formik";
import * as Yup from "yup";
import useAuth from "hooks/useAuth";
import toast from "react-hot-toast";
import FlexBetween from "components/flexbox/FlexBetween";
import FlexBox from "components/flexbox/FlexBox";
import FlexRowAlign from "components/flexbox/FlexRowAlign";
import AppTextField from "components/input-fields/AppTextField";
import { useState } from "react";

const Register = () => {
  const { register } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false); // Add loading state

  const initialValues = {
    name: "",
    email: "",
    password: "",
    repeatPassword: "",
    userType: "student",
    terms: false,
  };

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    email: Yup.string().email("Must be a valid email").max(255).required("Email is required"),
    password: Yup.string().min(6, "Password should be of minimum 6 characters length").required("Password is required"),
    repeatPassword: Yup.string().oneOf([Yup.ref('password'), null], 'Passwords must match').required('Repeat password is required'),
    terms: Yup.boolean().oneOf([true], "You must agree to the terms and conditions"),
  });

  const {
    errors,
    values,
    touched,
    handleBlur,
    handleChange,
    handleSubmit,
  } = useFormik({
    initialValues,
    validationSchema,
    onSubmit: async (values) => {
      setLoading(true); // Set loading to true on form submission
      try {
        await register(values.email, values.password, values.name);
        toast.success("You registered successfully");
        navigate("/dashboard");
      } catch (error) {
        toast.error("Registration failed. Please try again.");
      }
      setLoading(false); // Set loading back to false after form submission
    },
  });

  return (
    <FlexRowAlign sx={{ flexDirection: "column", height: { sm: "100%" } }}>
      <Card sx={{ padding: 4, maxWidth: 600, boxShadow: 1 }}>
        <FlexRowAlign flexDirection="column" mb={5}>
          <Box width={38} mb={1}>
            <img src="/static/logo/SkinEdu.png" width="100%" alt="Uko Logo" />
          </Box>
          <Typography variant="h5" fontWeight={700} mb={2}>
            Get started with SkinEdu
          </Typography>
        </FlexRowAlign>

        <form noValidate onSubmit={handleSubmit}>
          <FlexBetween flexWrap="wrap">
            {/* Other form fields */}

            <Box mt={4}>
              <LoadingButton
                loading={loading} // Pass loading state to LoadingButton
                fullWidth
                variant="contained"
                type="submit"
                disabled={!values.terms || loading}
              >
                Sign Up
              </LoadingButton>
            </Box>
          </FlexBetween>
        </form>

        <Typography variant="body2" mt={2} color="text.secondary" align="center">
          Already have an account?{' '}
          <Link to="/login" style={{ textDecoration: "none", color: "inherit" }}>
            Login
          </Link>
        </Typography>
      </Card>
    </FlexRowAlign>
  );
};

export default Register;
