import { Box, Card, styled, Table, TableBody, TableCell, TableHead, TableRow } from "@mui/material";
import { H5, Small } from "components/Typography";
import { useTranslation } from "react-i18next";
import ScrollBar from "simplebar-react";
const commonCSS = {
  minWidth: 120,
  "&:nth-of-type(2)": {
    minWidth: 170
  },
  "&:nth-of-type(3)": {
    minWidth: 80
  }
}; // Styled components

const HeadTableCell = styled(TableCell)(() => ({
  fontSize: 12,
  fontWeight: 600,
  "&:first-of-type": {
    paddingLeft: 0
  },
  "&:last-of-type": {
    paddingRight: 0
  }
}));
const BodyTableCell = styled(TableCell)(({
  theme
}) => ({
  fontSize: 12,
  fontWeight: 500,
  padding: 0,
  paddingLeft: "1rem",
  paddingTop: "0.7rem",
  "&:first-of-type": {
    paddingLeft: 0
  },
  "&:last-of-type": {
    paddingRight: 0
  },
  [theme.breakpoints.down("sm")]: { ...commonCSS
  },
  [theme.breakpoints.between(960, 1270)]: { ...commonCSS
  }
}));

const RecentOrders = () => {
  const {
    t
  } = useTranslation();
  return <Card sx={{
    padding: "2rem"
  }}>
      <H5>{t("Recent Orders")}</H5>

      <ScrollBar>
        <Table>
          <TableHead sx={{
          borderBottom: "1px solid",
          borderColor: "divider"
        }}>
            <TableRow>
              <HeadTableCell>Tracking No</HeadTableCell>
              <HeadTableCell>Product Name</HeadTableCell>
              <HeadTableCell>Price</HeadTableCell>
              <HeadTableCell>Total Order</HeadTableCell>
              <HeadTableCell>Total amount</HeadTableCell>
            </TableRow>
          </TableHead>

          <TableBody>
            {orderList.map((item, index) => <TableRow key={index}>
                <BodyTableCell>{item.orderNo}</BodyTableCell>
                <BodyTableCell>
                  <Box display="flex" alignItems="center">
                    <img src={item.image} alt="product title" width="40px" />
                    <Small ml="1rem">{item.name}</Small>
                  </Box>
                </BodyTableCell>
                <BodyTableCell>${item.price}</BodyTableCell>
                <BodyTableCell>
                  <Box sx={{
                borderRadius: 11,
                maxWidth: 55,
                padding: "0.3rem",
                textAlign: "center",
                color: "text.secondary",
                backgroundColor: "action.hover"
              }}>
                    {item.totalOrder}
                  </Box>
                </BodyTableCell>
                <BodyTableCell>{item.totalAmount}</BodyTableCell>
              </TableRow>)}
          </TableBody>
        </Table>
      </ScrollBar>
    </Card>;
};

const orderList = [{
  orderNo: "#JY7685",
  name: "Nike Air max 170",
  image: "/static/products/shoe-1.png",
  price: 654,
  totalOrder: 325,
  totalAmount: "$1,45,660"
}, {
  orderNo: "#JY7686",
  name: "Cactus Plant",
  image: "/static/products/bonsai.png",
  price: 654,
  totalOrder: 40,
  totalAmount: "$1,45,420"
}, {
  orderNo: "#JY7687",
  name: "Minimal Pot",
  image: "/static/products/airbud.png",
  price: 654,
  totalOrder: 57,
  totalAmount: "$45,660"
}, {
  orderNo: "#JY7688",
  name: "Adidas Blaze",
  image: "/static/products/shoe-2.png",
  price: 654,
  totalOrder: 125,
  totalAmount: "$12,660"
}];
export default RecentOrders;
