// usetitle.jsx
import React, { useContext, useEffect } from "react";
import { TitleContext } from "contexts/TitleContext";

const useTitle = (text) => {
  const { title, setTitle } = useContext(TitleContext);

  useEffect(() => {
    setTitle(text);
  }, [text, setTitle]);

  return title;
};

export default useTitle;
